    private void nadirlikDagit() {
        for (int i = 0; i < kostumlerHepsi.size(); i++) {
            KostumKristali kostumKristali = kostumlerHepsi.get(i);
            if (i != kostumKristali.getId())
                Log.e("Hatalı id", String.valueOf(kostumKristali.getId()));
            if (kostumKristali.getNadirlikId() == 1) {
                kostumlerN1.add(kostumKristali);
            } else if (kostumKristali.getNadirlikId() == 2) {
                kostumlerN2.add(kostumKristali);
            } else if (kostumKristali.getNadirlikId() == 3) {
                kostumlerN3.add(kostumKristali);
            } else if (kostumKristali.getNadirlikId() == 4) {
                kostumlerN4.add(kostumKristali);
            } else if (kostumKristali.getNadirlikId() == 5) {
                kostumlerN5.add(kostumKristali);
            } else if (kostumKristali.getNadirlikId() == 6) {
                kostumlerN6.add(kostumKristali);
            } else if (kostumKristali.getNadirlikId() == 7) {
                kostumlerN7.add(kostumKristali);
            }
        }
    }

    KostumKristali getKostumById(int id) {
        return kostumlerHepsi.get(id);
    }

    KostumKristali getKostum(int nadirlik, boolean includeLegacy, boolean includeNonAvailable) {
        ArrayList<KostumKristali> liste;
        if (nadirlik == 1) {
            liste = kostumlerN1;
        } else if (nadirlik == 2) {
            liste = kostumlerN2;
        } else if (nadirlik == 3) {
            liste = kostumlerN3;
        } else if (nadirlik == 4) {
            liste = kostumlerN4;
        } else if (nadirlik == 5) {
            liste = kostumlerN5;
        } else if (nadirlik == 6) {
            liste = kostumlerN6;
        } else {
            liste = kostumlerN7;
        }
        Random r = new Random();
        if (DEBUG) Log.e("Kostüm Listesi Boyutu", String.valueOf(liste.size()));
        int index = r.nextInt(liste.size());
        boolean changeLegacy = true;
        while (changeLegacy) {
            if (!includeLegacy) {
                if (liste.get(index).isHatiraIcerik()) {
                    index = r.nextInt(liste.size());
                } else changeLegacy = false;
            } else changeLegacy = false;
        }
        boolean changeNonAvailable = true;
        while (changeNonAvailable) {
            if (!includeNonAvailable) {
                if (liste.get(index).isMevcutOlmayanIcerik()) {
                    index = r.nextInt(liste.size());
                } else changeNonAvailable = false;
            } else changeNonAvailable = false;
        }
        return liste.get(index);
    }
